$.validator.setDefaults({
    highlight: function(e) {
        $(e).closest(".form-group").removeClass("has-success").addClass("has-error")
    },
    success: function(e) {
        e.closest(".form-group").removeClass("has-error").addClass("has-success")
    },
    errorElement: "span",
    errorPlacement: function(e, r) {
        e.appendTo(r.is(":radio") || r.is(":checkbox") ? r.parent().parent().parent() : r.parent())
    },
    errorClass: "help-block m-b-none",
    validClass: "help-block m-b-none"
}),
/*当采用submit提交时，打开*/
/*$.validator.setDefaults({  
    submitHandler: function() {  
        alert("submitted!");
        $("#signupForm").submit();
    }  
}); */ 
$().ready(function() {
    $("#addForm0").validate();
    var e = "<i class='fa fa-times-circle'></i> ";
    $("#signupForm").validate({
        rules: {
        	stu_name: {
                required: !0,
                minlength: 2
            },
            stu_num: {
                required: !0,
                minlength: 3
            },
            stu_sex:{
            	required:true
            },
            stu_date: {
            	required: true,  
                date: true  
            },
            stu_address:{
            	required: !0,
                minlength: 2
            },
            agree: "required"
        },
        messages: {
        	stu_name: {
                required: e + "请输入您的姓名",
                minlength: e + "姓名必须两个字符以上"
            },
            stu_num: {
                required: e + "请输入您的学号",
                minlength: e + "学号必须3个字符以上"
            },
            stu_sex:{
            	required:e + "你到底是男是女还是胡一菲啊~"
            },
            stu_date: {
                required: e + "请输入日期",
                date: true 
            },
            stu_address:{
            	required: e + "请输入您的家庭地址~"
            },
            agree: {
                required: e + "必须同意协议后才能注册",
                element: "#agree-error"
            }
        }
    })
});